import React, { Component } from 'react'
import './content.css'
import SlidingPane from './slidingPane'
import ClosedLeftMenu from './closedLeftMenu'
import DrawingCanvas from './drawingCanvas'
import SlideContent from './slideContent'
import BackgroundImages from './backgroundImages'
import { DragDropContext } from 'react-beautiful-dnd';
import { Droppable } from 'react-beautiful-dnd';
import UploadFile from './uploadFile';

class Content extends Component {
  state = {
    isModalOpen: false,
    selectedMenuOption: '',
    draggingRowId:null,
    starredProjects:{}
    
  }

  toggleState = e => {
    this.setState({
      isModalOpen: !this.state.isModalOpen
    })
  }
  selectMenuOption = menuItem => {
    console.log('selectMenuOption', menuItem)
    this.setState({
      selectedMenuOption: menuItem,
      isModalOpen: !this.state.isModalOpen
    },()=>{
      if(this.state.selectedMenuOption === 'file'){
          console.log('file pick success')
      }
      else{
        console.log('file pick unsuccess')
        
      }
    })
  }

  onBeforeDragStart = () => {
    /* ... */
  }

  onDragStart = (event) => {
    // event.dataTransfer.setData("text", event.target.id);
     console.log('onstart event ==> ', event);
    // console.log('nativeEvent ==> ', event.nativeEvent); //<- gets native JS event
    /* ... */
  }
  onDragUpdate = () => {
    /* ... */
  }
  onDragEnd = (result) => {
    // the only one that is required
    const { destination, source, reason } = result;
     console.log('source >>', source);
    //  console.log('Droppable item ID>>',destination.droppableId === source.droppableId);
    // console.log('Index >>',destination.index === source.index);
    // Not a thing to do...
    if (!destination || reason === 'CANCEL') {
      this.setState({
        draggingRowId: null,
      });
      return;
    }

    if (
      destination.droppableId === source.droppableId &&
      destination.index === source.index
    ) {
      console.log('Droppable item ID>>',destination.droppableId === source.droppableId);
      console.log('Index >>',destination.index === source.index);
    }

    // const starredProjects = Object.assign([], this.state.starredProjects);
    // console.log('StarredProject >>', starredProjects)
    // const project = this.state.starredProjects[source.index];
    // console.log('Source index >>', source.index);
    // starredProjects.splice(source.index, 1);
    // starredProjects.splice(destination.index, 0, project);
    // this.setState({
    //   starredProjects
    // });

  }

  render () {
    const selectedOptionContent = 
    {
      background: <BackgroundImages/>,
      file: <UploadFile/>
    }
    [this.state.selectedMenuOption]
    // console.log('selected content background>>', selectedOptionContent)
    // console.log('menu option array >>',[this.state.selectedMenuOption]);
    return (
      <section className='mainSection'>
        <DragDropContext
          onBeforeDragStart={this.onBeforeDragStart}
          onDragStart={this.onDragStart}
          onDragUpdate={this.onDragUpdate}
          onDragEnd={this.onDragEnd}
        >
        <Droppable droppableId='droppable' type='DRAWING'>
            {(provided, snapshot) => (
            <div
                className='droppableSection'
                ref={provided.innerRef}
                style={{
                backgroundColor: snapshot.isDraggingOver
                    ? 'white'
                    : '#ececec'
                }}
                {...provided.droppableProps}
            >  
                {console.log('Provided >>', provided)}
                <div className='menuSection'>
                    <ClosedLeftMenu selectMenuOption={this.selectMenuOption} />
                </div>

                    {this.state.isModalOpen && (
                      
                    <SlidingPane
                        id='modal'
                        isOpen={this.state.isModalOpen}
                        onClose={this.toggleState}
                    >
                        <SlideContent content={selectedOptionContent} />
                     </SlidingPane>
                     )} 
                <div className='canvasSection'>
                    <div className='drawingCanvas'>
                      <h1>Hello To The world</h1>
                    </div>  
                       
                </div>
            </div>
            )}
        </Droppable>
        </DragDropContext>
      </section>
    )
  }
}

export default Content
